import React, { useState, useEffect } from 'react';
import './App.css';

/*
const GIFS = [
  'https://media4.giphy.com/media/pO1H8mAU7geAw/giphy.gif?cid=ecf05e47q0jmkvkgmqe8ps46xtyuzk6dk6uh1kys8kzvx3r5&rid=giphy.gif&ct=g',
  'https://media1.giphy.com/media/EPcvhM28ER9XW/giphy.gif?cid=ecf05e47hxr2148a74z0n62wiuvmdkeirr9if02d7bsv1tl3&rid=giphy.gif&ct=g'

]

const DIFFERENT_GIFS = [
  'https://media3.giphy.com/media/tFUdJEX8jNbe8/giphy.gif?cid=ecf05e47824d5iokkst93y1quk3ur42b07vubvghcj8m0tme&rid=giphy.gif&ct=g'
]
*/

const apiUrl = 'https://api.giphy.com/v1/gifs/search?api_key=dIJrma20pSU6ymMwWnDbiaT7NFHeAGVa&q=panda&limit=10&offset=0&rating=G&lang=en'
function App() {
  const [gifs, setGifs] = useState([]);

  useEffect(function () {
    console.log('Actualizando los gifs');
    fetch(apiUrl)
      .then(res => res.json())
      .then(response => {
        const {data = []} = response
        const gifs = data.map(image => image.images.downsized_medium.url)
        setGifs(gifs)
      })

  },[])

  return (
    <div className="App">
      <section className="App-content">
        {
          gifs.map(singleGif => <img src={singleGif} />)
        }
      </section>
    </div>
  );
}

export default App;
