import React, {useEffect,useState} from "react";
import Gif from "./Gif";
impo

export default function ListOfGifs({ keyword }) {
    const [gifs, setGifs] = useState([]);

    useEffect(function () {
      getGifs({ keyword}).then(gifs => setGifs(gifs));
    }, [])

    return gifs.map(({ title, id, url }) =>
        <Gif
            key={id}
            title={title}
            url={url}
            id={id}
        />
    )
}
